document.addEventListener('DOMContentLoaded',function (){const ctx = document.getElementById('myChart');if (ctx && storageData.data){const storageInfo = storageData.data;const labels = Object.keys(storageInfo).filter(key => key !== 'Total');const data = labels.map(label => storageInfo[label]);const myChart = new Chart(ctx.getContext('2d'),{type:'pie',data:{labels:labels,datasets:[{label:'Storage Usage (MB)',data:data,backgroundColor:[ '#9bc4ff','#55d490','#13b2da','#256df0',],borderColor:[ 'rgba(173,216,230,1)','rgba(100,149,237,1)','rgba(0,123,255,1)','rgba(70,130,180,1)',],borderWidth:1}]},options:{responsive:true,plugins:{legend:{position:'top',},},},});}else{console.error('Storage data or canvas not available.');}});